import { Component } from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  username = '';
  password = '';
  error = '';
  returnUrl: string | null = null;

  constructor(private auth: AuthService, private router: Router, private route: ActivatedRoute) {
    this.returnUrl = this.route.snapshot.queryParamMap.get('returnUrl');
  }

  submit(){
    this.error = '';
    if(!this.username || !this.password){
      this.error = 'Username and password are required.';
      return;
    }
    const ok = this.auth.login(this.username.trim(), this.password);
    if(ok){
      const target = this.returnUrl ?? '/employees';
      this.router.navigateByUrl(target);
    } else {
      this.error = 'Invalid credentials.';
    }
  }
}
<div class="card-like" style="max-width:420px;margin:2rem auto;">
  <h4 class="text-center">Sign in</h4>
  <form (ngSubmit)="submit()">
    <div class="form-group">
      <label>Username</label>
      <input [(ngModel)]="username" name="username" class="form-control" required />
    </div>
    <div class="form-group">
      <label>Password</label>
      <input [(ngModel)]="password" name="password" type="password" class="form-control" required />
    </div>
    <div *ngIf="error" class="text-danger">{{error}}</div>
    <div style="margin-top:1rem; display:flex; gap:8px;">
      <button class="btn btn-primary" type="submit">Login</button>
      <button type="button" class="btn btn-secondary" (click)="username='';password='';error=''">Clear</button>
    </div>
  </form>
</div>
