import { Component } from '@angular/core';
import { AuthService } from '../auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent {
  isNavOpen = false;
  isUserMenuOpen = false;

  constructor(public auth: AuthService, private router: Router) {}

  toggleNav() {
    this.isNavOpen = !this.isNavOpen;
  }

  toggleUserMenu() {
    this.isUserMenuOpen = !this.isUserMenuOpen;
  }

  closeUserMenu() {
    this.isUserMenuOpen = false;
  }

  closeNav() {
    this.isNavOpen = false;
    this.closeUserMenu();
  }

  logout() {
    this.auth.logout();
    this.closeNav();
  }

  goToLogin() {
    this.router.navigate(['/login']);
    this.closeNav();
  }
}
<nav class="navbar">
  <div class="navbar-container">
    <!-- Logo -->
    <div class="navbar-logo">
      <span class="logo-link">
        <span class="logo-icon">üìä</span>
        <span class="logo-text">Employee Management System</span>
      </span>
    </div>

    <!-- Hamburger Menu -->
    <div class="hamburger" [class.active]="isNavOpen" (click)="toggleNav()">
      <span></span>
      <span></span>
      <span></span>
    </div>

    <!-- Navigation Links -->
    <div class="navbar-menu" [class.active]="isNavOpen">
      <div class="nav-center" *ngIf="auth.isAuthenticated()">
        <a routerLink="/employees" 
           routerLinkActive="active" 
           (click)="closeNav()"
           class="nav-link">
          <span class="nav-icon">üë•</span> Employee List
        </a>
        <a routerLink="/create-employee" 
           routerLinkActive="active" 
           (click)="closeNav()"
           class="nav-link">
          <span class="nav-icon">‚ûï</span> Add Employee
        </a>
      </div>

      <div class="nav-right">
        <div *ngIf="!auth.isAuthenticated()" class="nav-item">
          <button class="nav-link btn-login" (click)="goToLogin()">
            <span class="nav-icon">üîê</span> Login
          </button>
        </div>
        <div *ngIf="auth.isAuthenticated()" class="user-menu">
          <div class="user-dropdown-container">
            <span class="user-display" (click)="toggleUserMenu()" [class.active]="isUserMenuOpen">
              <span class="user-icon">üë§</span>
              <span class="username">{{auth.currentUser()}}</span>
            </span>
            <div class="user-dropdown-menu" *ngIf="isUserMenuOpen">
              <button class="dropdown-item logout-item" (click)="logout()">
                <span class="item-icon">üö™</span> Logout
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</nav>
