import { Component } from '@angular/core';
import { Employee } from '../employee';
import { ActivatedRoute, Router } from '@angular/router';
import { EmployeeService } from '../employee.service';

@Component({
  selector: 'app-employee-details',
  templateUrl: './employee-details.component.html',
  styleUrls: ['./employee-details.component.css']
})
export class EmployeeDetailsComponent {
  id! : number 
  employee!: Employee 
  constructor(private route: ActivatedRoute, private employeeService: EmployeeService, private router: Router) {}

  ngOnInit(): void {
    this.id = this.route.snapshot.params['id'];
    this.employee=new Employee();
    this.employeeService.getEmployeeById(this.id).subscribe({
      next: (data) => {
        this.employee=data;
      },
      error: (err) => {
        console.error('Error loading employee', err);
        alert('Failed to load employee details');
        this.router.navigate(['/employees']);
      }
    })
  }

  updateEmployee(id: number | undefined) {
    if(id) this.router.navigate(['update-employee', id]);
  }

  deleteEmployee(id: number | undefined) {
    if(!id) return;
    if(!confirm('Are you sure you want to delete this employee? This action cannot be undone.')) return;
    this.employeeService.deleteEmployee(id).subscribe({
      next: () => {
        alert('Employee deleted successfully.');
        this.router.navigate(['/employees']);
      },
      error: (err) => {
        console.error('Delete failed', err);
        alert('Failed to delete employee');
      }
    });
  }

  employeeList() {
    this.router.navigate(['/employees']);
  }
}
<div class="container mt-4">
<div class="details-card card-like">
  <div class="details-header">
    <h2>Employee Details</h2>
    <div class="employee-id-badge">ID: #{{employee.id}}</div>
  </div>

  <div class="details-grid">
    <div class="detail-item">
      <label>First Name</label>
      <div class="detail-value">{{employee.firstName}}</div>
    </div>

    <div class="detail-item">
      <label>Last Name</label>
      <div class="detail-value">{{employee.lastName}}</div>
    </div>

    <div class="detail-item">
      <label>Email Address</label>
      <div class="detail-value">
        <a href="mailto:{{employee.emailId}}">{{employee.emailId}}</a>
      </div>
    </div>

    <div class="detail-item">
      <label>Department</label>
      <div class="detail-value dept-badge">{{employee.department}}</div>
    </div>
  </div>

  <div class="details-actions">
    <button (click)="updateEmployee(employee.id)" class="btn btn-warning">Edit</button>
    <button (click)="deleteEmployee(employee.id)" class="btn btn-danger">Delete</button>
    <button (click)="employeeList()" class="btn btn-secondary">Back to List</button>
  </div>
</div>
</div>
