import { Component, OnInit } from '@angular/core';
import { EmployeeService } from '../employee.service';
import { Employee } from '../employee';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {
  employees: Employee[] = [];
  totalEmployees = 0;
  recentEmployees: Employee[] = [];
  departmentStats: any[] = [];
  loading = true;
  error = '';

  constructor(private employeeService: EmployeeService) {}

  ngOnInit(): void {
    this.loadDashboardData();
  }

  loadDashboardData(): void {
    this.employeeService.getEmployeesList().subscribe({
      next: (data) => {
        this.employees = data;
        this.totalEmployees = this.employees.length;
        this.recentEmployees = this.employees.slice(0, 5);
        this.calculateDepartmentStats();
        this.loading = false;
      },
      error: (error) => {
        console.error('Error loading employees:', error);
        this.error = 'Failed to load dashboard data';
        this.loading = false;
      }
    });
  }

  calculateDepartmentStats(): void {
    const departments: { [key: string]: number } = {};
    
    this.employees.forEach(emp => {
      const dept = emp.department || 'Unassigned';
      departments[dept] = (departments[dept] || 0) + 1;
    });

    this.departmentStats = Object.entries(departments).map(([dept, count]) => ({
      name: dept,
      count: count,
      percentage: ((count / this.totalEmployees) * 100).toFixed(1)
    }));
  }

  getStatColor(index: number): string {
    const colors = ['#3498db', '#2ecc71', '#e74c3c', '#f39c12', '#9b59b6'];
    return colors[index % colors.length];
  }
}
<div class="dashboard-container">
  <div class="dashboard-header">
    <h1>üìä Dashboard</h1>
    <p>Employee Management System Overview</p>
  </div>

  <!-- Loading State -->
  <div *ngIf="loading" class="loading-spinner">
    <div class="spinner"></div>
    <p>Loading dashboard data...</p>
  </div>

  <!-- Error State -->
  <div *ngIf="error && !loading" class="error-message">
    <span>‚ö†Ô∏è {{ error }}</span>
  </div>

  <!-- Dashboard Content -->
  <div *ngIf="!loading && !error" class="dashboard-content">
    <!-- Statistics Cards -->
    <div class="stats-section">
      <div class="stat-card total-employees">
        <div class="stat-icon">üë•</div>
        <div class="stat-info">
          <h3>Total Employees</h3>
          <p class="stat-number">{{ totalEmployees }}</p>
        </div>
      </div>

      <div class="stat-card active-employees">
        <div class="stat-icon">‚úÖ</div>
        <div class="stat-info">
          <h3>Active</h3>
          <p class="stat-number">{{ totalEmployees }}</p>
        </div>
      </div>

      <div class="stat-card new-employees">
        <div class="stat-icon">‚ú®</div>
        <div class="stat-info">
          <h3>Recent Hires</h3>
          <p class="stat-number">{{ recentEmployees.length }}</p>
        </div>
      </div>

      <div class="stat-card departments">
        <div class="stat-icon">üè¢</div>
        <div class="stat-info">
          <h3>Departments</h3>
          <p class="stat-number">{{ departmentStats.length }}</p>
        </div>
      </div>
    </div>

    <!-- Department Statistics -->
    <div class="departments-section">
      <h2>Department Distribution</h2>
      <div class="department-list">
        <div *ngFor="let dept of departmentStats" class="department-item">
          <div class="dept-header">
            <span class="dept-name">{{ dept.name }}</span>
            <span class="dept-count">{{ dept.count }} employees</span>
          </div>
          <div class="dept-bar">
            <div class="dept-progress" [style.width]="dept.percentage + '%'" [style.backgroundColor]="getStatColor(departmentStats.indexOf(dept))"></div>
          </div>
          <div class="dept-percentage">{{ dept.percentage }}%</div>
        </div>
      </div>
    </div>

    <!-- Recent Employees -->
    <div class="recent-section">
      <h2>Recent Employees</h2>
      <div class="recent-employees-list">
        <div *ngIf="recentEmployees.length === 0" class="no-data">
          <p>No employees found</p>
        </div>
        <div *ngFor="let emp of recentEmployees" class="employee-card">
          <div class="employee-header">
            <span class="employee-avatar">{{ emp.firstName?.charAt(0) }}{{ emp.lastName?.charAt(0) }}</span>
            <div class="employee-info">
              <h4>{{ emp.firstName }} {{ emp.lastName }}</h4>
              <p>{{ emp.emailId }}</p>
            </div>
          </div>
          <div class="employee-details">
            <span class="detail-badge" *ngIf="emp.department">{{ emp.department }}</span>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
