import { Component } from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  username = '';
  password = '';
  error = '';
  isLoading = false;
  returnUrl: string | null = null;

  constructor(private auth: AuthService, private router: Router, private route: ActivatedRoute) {
    this.returnUrl = this.route.snapshot.queryParamMap.get('returnUrl');
  }

  submit(form?: any) {
    if (!form || form.invalid) return;
    this.error = '';
    this.isLoading = true;

    this.auth.login(this.username.trim(), this.password).subscribe(
      (response) => {
        const target = this.returnUrl ?? '/employees';
        this.router.navigateByUrl(target);
      },
      (error) => {
        console.error('Login error:', error);
        this.error = error.error?.message || 'Invalid credentials. Use admin / admin123';
        this.isLoading = false;
      }
    );
  }
}
<div class="login-container">
  <div class="login-card card-like">
    <div class="login-header">
      <h3>Employee Management System</h3>
      <p class="login-subtitle">Sign in to continue</p>
    </div>
    <form #loginForm="ngForm" (ngSubmit)="submit(loginForm)">
      <div class="form-group">
        <label>Username</label>
        <input 
          [(ngModel)]="username" 
          name="username" 
          class="form-control" 
          required 
          placeholder="Enter username"
          #usernameCtrl="ngModel"
        />
        <div *ngIf="usernameCtrl.invalid && usernameCtrl.touched" class="text-danger small-text">Username is required.</div>
      </div>
      <div class="form-group">
        <label>Password</label>
        <input 
          [(ngModel)]="password" 
          name="password" 
          type="password" 
          class="form-control" 
          required 
          placeholder="Enter password"
          #passwordCtrl="ngModel"
        />
        <div *ngIf="passwordCtrl.invalid && passwordCtrl.touched" class="text-danger small-text">Password is required.</div>
      </div>
      <div *ngIf="error" class="alert alert-danger alert-sm" role="alert">{{error}}</div>
      <button class="btn btn-primary btn-block" type="submit" [disabled]="!loginForm.valid || isLoading">{{ isLoading ? 'Signing in...' : 'Sign in' }}</button>
    </form>
    <div class="login-hint">
      <small>Demo credentials: admin / admin123</small>
    </div>
  </div>
</div>
